"""Atomic task status updates."""

from __future__ import annotations

import re
from pathlib import Path

from .models import TaskStatus


def update_task_status(
    path: Path,
    task_description: str,
    new_status: TaskStatus,
    adw_id: str | None = None,
    commit_hash: str | None = None,
    error_message: str | None = None,
) -> bool:
    """Update task status in tasks.md."""
    if not path.exists():
        return False

    content = path.read_text()
    lines = content.split("\n")
    desc_escaped = re.escape(task_description.strip())
    updated = False

    for i, line in enumerate(lines):
        if not re.search(rf"\]\s*.*{desc_escaped}", line, re.IGNORECASE):
            continue

        # Build status marker
        if new_status == TaskStatus.PENDING:
            marker = "[]"
        elif new_status == TaskStatus.BLOCKED:
            marker = "[‚è∞]"
        elif new_status == TaskStatus.IN_PROGRESS:
            marker = f"[üü°, {adw_id}]" if adw_id else "[üü°]"
        elif new_status == TaskStatus.DONE:
            parts = ["‚úÖ"]
            if commit_hash:
                parts.append(commit_hash[:9])
            if adw_id:
                parts.append(adw_id)
            marker = f"[{', '.join(parts)}]"
        elif new_status == TaskStatus.FAILED:
            marker = f"[‚ùå, {adw_id}]" if adw_id else "[‚ùå]"
        else:
            continue
